import re
import json
from util.logUtil import Log

logger = Log('regFindString').getlogger()


class regFindString:
    def __init__(self, str, regs):
        self.str = str
        self.regs = regs

    def find(self):
        res = {}
        ''' 20190522修改，捕获string转字典异常，并打印错误日志'''
        try:
            # string转为字典格式
            reg = json.loads(self.regs)
            for i in reg.keys():
                r = re.findall(reg[i], self.str)
                if r:
                    res[i] = r[0]
                else:
                    res[i] = ''
        except json.decoder.JSONDecodeError as e:
            print("re字段不是标准json，请确认！,报错信息如下：", e)
            # logger.error("re字段不是标准json，请确认！,报错信息如下： %s" % e)

        finally:
            return res


